mapper.select:
SELECT *
FROM user
WHERE email = #{email} AND social_name=#{socialName}

/* 일반 로그인 */ 
SELECT	
	*		
	FROM USER
WHERE email = #{email}

/* 회원 정보 조회 */ 
SELECT * 
	FROM USER
WHERE id = #{id}

mapper.update:
/* 회원 정보 수정 */ 
UPDATE USER SET

	NAME = #{name},


	PASSWORD = #{password}

WHERE ID = #{id}

mapper.delete:
/*회원 정보 삭제*/
DELETE 
	FROM USER
WHERE ID = #{id}

mapper.select:
/*회원 리스트 총갯수*/
SELECT COUNT(*)
	FROM USER

